mapper.select:
select g.idgrupo as gid, g.numero as gnumero, c.id as cid, c.nombre as cnombre, c.creditosacademicos as ccreditosacademicos, c.horasaprovadasmonitoria as chorasaprovadasmonitoria,
s.numero as snumero, s.ano as sano, s.fechainicio as sfechainicio, s.fechafin as sfechafin
from grupo as g, curso as c, semestre as s where c.id = g.curso_id and s.numero = g.semestre_numero and curso_id = #{curso} and semestre_numero = #{semestre}

select g.idgrupo as gid, g.numero as gnumero, c.id as cid, c.nombre as cnombre, c.creditosacademicos as ccreditosacademicos, c.horasaprovadasmonitoria as chorasaprovadasmonitoria,
s.numero as snumero, s.ano as sano, s.fechainicio as sfechainicio, s.fechafin as sfechafin
from grupo as g, curso as c, semestre as s where c.id = g.curso_id and s.numero = g.semestre_numero and g.profesor_persona_id = #{idprofesor} and semestre_numero = #{semestre}

select count(a.idasesoria)
    from asesoria as a, grupo as g
    where a.grupo_idgrupoasistente = g.idgrupo and g.idgrupo = #{grupo}
